package helloworld;

public class Whileloops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String[] names = {"David" ,"Dominic","Robert","Jimboy","Jimboy"};
		
		int i = 0;
		
		while(i < 5) {
			System.out.println(names[i]);
			i++;
			
		}
		
		
		
		
	}

}
